// src/IciclePlot.js
import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const IciclePlot = ({ data }) => {
  const ref = useRef();

  useEffect(() => {
    const width = 960;
    const height = 500;

    const color = d3.scaleOrdinal(d3.schemeCategory10);

    const partition = data => {
      const root = d3.hierarchy(data)
        .sum(d => d.value)
        .sort((a, b) => b.value - a.value);
      return d3.partition()
        .size([height, width])
        (root);
    };

    const root = partition(data);

    const svg = d3.select(ref.current)
      .attr('width', width)
      .attr('height', height);

    svg.selectAll("*").remove(); // Clear previous contents if any

    const rects = svg.selectAll("rect")
      .data(root.descendants())
      .enter().append("rect")
      .attr("class", "node")
      .attr("x", d => d.y0)
      .attr("y", d => d.x0)
      .attr("width", d => d.y1 - d.y0)
      .attr("height", d => d.x1 - d.x0)
      .attr("fill", d => color((d.children ? d : d.parent).data.name))
        .style("stroke", "#fff")
        .style("cursor", "pointer")
        .on("mouseover", function (){
          d3.select(this).style("opacity", 0.8);
        })
        .on("mouseout", function (){
          d3.select(this).style("opacity",1);
        })

      .on("click", (event, d) => {
        svg.transition()
          .duration(750)
          .tween("scale", () => {
            const xd = d3.interpolate(root.y0, d.y0),
                  yd = d3.interpolate(root.x0, d.x0),
                  kx = width / (d.y1 - d.y0),
                  ky = height / (d.x1 - d.x0);
            return t => {
              root.y0 = xd(t);
              root.x0 = yd(t);
              svg.selectAll("rect")
                .attr("x", d => d.y0)
                .attr("y", d => d.x0)
                .attr("width", d => d.y1 - d.y0)
                .attr("height", d => d.x1 - d.x0);
            };
          });
      });

    svg.selectAll("text")
      .data(root.descendants())
      .enter().append("text")
      .attr("class", "label")
      .attr("x", d => d.y0 + 4)
      .attr("y", d => (d.x0 + d.x1) / 2)
      .text(d => d.data.name)
      .style("fill", "#fff")
        .style("font", "10px sans-serif")
        .style("text-anchor", "start")
        .style("dominant-baseline", "middle");

  }, [data]);

  return (
    <svg ref={ref}></svg>
  );
};

export default IciclePlot;
