// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import IciclePlot from './IciclePlot';


import BarChart from "./BarChart";
import LineChart from "./LineChart";
import IciclePlot from "./IciclePlot";

const App = () => {
  const [data, setData] = useState(null);
  const [activeTab, setActiveTab] = useState('icicle');
    const [plots, setPlots] = useState([data, data]); // Example: Two plots

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/data')
      .then(response => {
          const fetchedData = response.data;
          setData(fetchedData);
          setPlots([fetchedData, fetchedData]) //Set two plots with the fetched data
          console.log('Data fetched:', response.data)
          setData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

    const renderTabContent = () => {
    switch (activeTab) {
      case 'icicle':
        return (
            <div className="icicle-plots-container">
                {plots.map((plotData, index) => (
                    <div key={index} className="icicle-plot-container">
                        <IciclePlot data={plotData}/>
                    </div>
                ))}
            </div>
        );
        case 'bar':
            return <BarChart/>;
        case 'line':
            return <LineChart/>;
        default:
            return <p>Select a tab</p>;
    }
    };

    return (
        <div>
            <h1>Visualization Tabs</h1>
            <div className="tabs">
            <button onClick={() => setActiveTab('icicle')}>Icicle Plot</button>
          <button onClick={() => setActiveTab('bar')}>Bar Chart</button>
          <button onClick={() => setActiveTab('line')}>Line Chart</button>
        </div>
        <div className="tab-content">
          {renderTabContent()}
        </div>
      </div>
  );
};

export default App;
